package modusSystemsPattern;

public class p14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		int b=1;
		for(int i=1;i<=n;i++) {
			for(int j=1;j<=i;j++) {
				System.out.print(b+" ");
				b++;
				
			}
			System.out.println();
		}

	}

}
